import React, { useContext } from "react";
import classes from "./Button.module.css";
import { signInWithGoogle } from "../../services/auth";
import { UserContext } from "../../context/userContext";

export const SignInSignUpBtn = () => {
  const [, setUser] = useContext(UserContext).user;

  const buttonClickHandler = async () => {
    let signedInUser = await signInWithGoogle();
    if (signedInUser) {
      setUser(signedInUser);
    }
  };

  return (
    <button
      onClick={buttonClickHandler}
      id="signIn__signUp__btn"
      className={classes.signIn__signUp__btn}
    >
      sign in | sign up
    </button>
  );
};

export const CreatePostUploadBtn = ({ caption, onUploadHandler, progress }) => {
  return (
    <button
      className={classes.createPost_upload_btn}
      onClick={onUploadHandler}
      style={{
        color: caption ? "#000" : "lightgrey",
        border: `1px solid ${caption ? "#000" : "lightgrey"}`,
        background: "transparent",
        cursor: "pointer",
        fontSize: "0.9rem",
        borderRadius: "1.5rem",
        padding: "0.3rem 0.7rem",
        onMouseOver: "color='#0F0'",
      }}
    >
      {`Upload ${progress !== 0 ? progress : ""}`}
    </button>
  );
};
