import React, { useState } from "react";
import classes from "./CreateComment.module.css";
import { Create_Post_Btn } from "../../../ui/button/Button";
import { useRef } from "react";

const CreateComment = () => {
  const [comment, setComment] = useState("");

  const commentRef = useRef();

  return (
    <div className={classes.create_comment_container}>
      <textarea
        onChange={() => {
          setComment(commentRef.current.value);
        }}
        name="create_comment_field"
        id="create_comment_field"
        placeholder="enter comment here.."
        value={comment}
        ref={commentRef}
        rows="1.5"
        className={classes.createComment_textarea}
      ></textarea>
      <Create_Post_Btn comment={comment} />
    </div>
  );
};

export default CreateComment;
