export const Post_Delete_Btn = ({ post, onDeleteHandler }) => {
  return (
    <button
      className={classes.post_Delete_Btn}
      onClick={onDeleteHandler}
      style={{
        color: post ? "#fff" : "lightgrey",
        border: `1px solid ${post ? "#fff" : "lightgrey"}`,
        background: post ? "#303030" : "transparent",
        cursor: "pointer",
        fontSize: "0.9rem",
        borderRadius: "1.5rem",
        padding: "0.3rem 0.7rem",
      }}
    >
      Delete
    </button>
  );
};

