import React from "react";
import classes from "./CreateComment.module.css";
import { Create_Post_Btn } from "../../../ui/button/Button";
import { useRef } from "react";

const CreateComment = () => {
  const commentRef = useRef();
  return (
    <div className={classes.create_comment_container}>
      <textarea
        onChange={() => {
          setCaption(postRef.current.value);
        }}
        name="create_comment_field"
        id="create_comment_field"
        placeholder="enter comment here.."
        value={caption}
        ref={postRef}
        cols="70"
        rows="7"
        className={classes.createPost_textarea}
      ></textarea>
      <Create_Post_Btn />
    </div>
  );
};

export default CreateComment;
