import React, { useContext, useState, useEffect } from "react";
import classes from "./Feed.module.css";
import { Post_Delete_Btn } from "../../ui/button/Button";
import { UserContext } from "../../context/userContext";
import Post from "./post/Post";
import { db } from "../../firebase/firebase";

const Feed = () => {
  const [user] = useContext(UserContext).user;
  const [posts, setPosts] = useState([]);
  let username = user.email.replace("@gmail.com", "");

  useEffect(() => {
    db.collection("posts").onSnapshot((snapshot) => {
      setPosts(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          post: doc.data(),
        }))
      );
    });
  }, []);

  return (
    <div className={classes.feed}>
      {posts.map(({ id, post }) => {
        return (
          <div className={classes.feed_container}>
            <div className={classes.feed_left_side}>
              <img
                src={user.photoURL}
                alt="profile_pic"
                className={classes.feed_img}
              />
              <p className={classes.feed_username}>{username}</p>
            </div>
            <div className={classes.feed_right_side}>
              <Post_Delete_Btn />
            </div>
            <Post
              key={id}
              id={id}
              profileUrl={post.profileUrl}
              username={post.username}
              imageUrl={post.imageUrl}
              caption={post.caption}
              comments={post.comments}
              user={user}
            />
          </div>
        );
      })}
    </div>
  );
};

export default Feed;
