import React, { Fragment, useState } from "react";
import CreateComment from "../create-comment/CreateComment";
import classes from "./Post.module.css";
import MessageIcon from "@material-ui/icons/Message";
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";

const Post = ({
  id,
  profileUrl,
  username,
  imageUrl,
  caption,
  comments,
  user,
  timestamp,
}) => {
  const [showCommentInput, setShowCommentInput] = useState(false);
  const [likeClicked, setLikeClicked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [commentIconToolTip, setCommentIconToolTip] = useState(false);

  const onClickCommentInput = () => {
    setShowCommentInput((prevState) => !prevState);
  };

  const onLikeClicked = () => {
    setLikeClicked(true);
    setLikeCount(likeCount + 1);
  };

  const onMouseOverMessageIcon = () => {
    setCommentIconToolTip(true);
  };

  const onMouseOutMessageIcon = () => {
    setCommentIconToolTip(false);
  };

  const toolTipStyle = {
    display: commentIconToolTip ? "block" : "none",
    fontSize: "0.5rem",
    position: "absolute",
    top: inherit,
  };

  return (
    <div className={classes.post_container}>
      <img src={imageUrl} alt="posted_img" className={classes.post_img} />
      <div className={classes.caption_container}>
        <p className={classes.poster_username}>{username}</p>
        <p className={classes.poster_caption}>{caption}</p>
      </div>
      {comments ? (
        comments.map((comment) => (
          <div className={classes.post_comments}>
            <div className={classes.comment_username}>{comment.username}</div>
            <div className={classes.comment_comment}>{comment.comment}</div>
          </div>
        ))
      ) : (
        <></>
      )}

      <div className={classes.msg_icon_container}>
        <p style={toolTipStyle}>I am a msg</p>
        <MessageIcon
          onMouseOver={onMouseOverMessageIcon}
          onMouseOut={onMouseOutMessageIcon}
          className={classes.msg_icon}
          onClick={onClickCommentInput}
        />
        <div className={classes.heart_icon} onClick={onLikeClicked}>
          {likeClicked ? (
            <FavoriteIcon
              onClick={() => setLikeCount(likeCount + 1)}
              style={{ fill: "red" }}
            />
          ) : (
            <FavoriteBorderIcon />
          )}
        </div>
        <p className={classes.like}>{`${likeCount} ${
          likeCount === 0 ? "like" : likeCount === 1 ? "like" : "likes"
        }`}</p>
      </div>
      {showCommentInput && <CreateComment id={id} comments={comments} />}
    </div>
  );
};

export default Post;
