import React, { useState, useContext, useRef } from "react";
import classes from "./CreateComment.module.css";
import { Create_Post_Btn } from "../../../ui/button/Button";
import { UserContext } from "../../../context/userContext";
import { db } from "../../../firebase/firebase";

const CreateComment = ({ id, comments }) => {
  const [comment, setComment] = useState("");
  const [commentList, setCommentList] = useState(comments ? comments : []);
  const [user] = useContext(UserContext).user;

  const commentRef = useRef();

  const addComment = () => {
    if (comment !== "") {
      commentList.push({
        comment,
        username: user.email.replace("@gmail.com", "").toLowerCase(),
      });

      db.collection("posts")
        .doc(id)
        .update({
          comments: commentList,
        })
        .then(() => console.log("comment added"))
        .catch((error) => console.log(error.message));
    }
    setComment("");
  };

  return (
    <div className={classes.create_comment_container}>
      <div className={classes.comment}>{commentList}</div>
      <textarea
        onChange={() => {
          setComment(commentRef.current.value);
        }}
        name="create_comment_field"
        id="create_comment_field"
        placeholder="enter comment here.."
        value={comment}
        ref={commentRef}
        rows="2"
        className={classes.createComment_textarea}
      ></textarea>
      <Create_Post_Btn comment={comment} addComment={addComment} />
    </div>
  );
};

export default CreateComment;
