import React, { useContext, useRef, useState } from "react";
import classes from "./CreatePost.module.css";
import { UserContext } from "../../context/userContext";

const CreatePost = () => {
  const [user, setUser] = useContext(UserContext).user;
  const [post, setPost] = useState(null);
  const postRef = useRef();

  const onChangeHandler = (e) => {
    let test = e.target.value;
  };

  return (
    <div className={classes.createPost}>
      {user && (
        <div className={classes.createPost_main_container}>
          <p>Create a post</p>
          <div className={classes.createPost_textarea_container}>
            <textarea
              onChange={() => {
                setPost(postRef.current.value);
              }}
              name="create_post_field"
              id="create_post_field"
              placeholder="Enter your post.."
              value={post}
              ref={postRef}
              cols="70"
              rows="7"
              maxWidth=""
              className={classes.createPost_textarea}
            ></textarea>
          </div>
          <div className="image_upload_container">
            <label htmlFor="image_uploader"></label>
            <input
              id="image_uploader"
              type="file"
              accept="image/*"
              onChange={onChangeHandler}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
