import React, { useState } from "react";
import { useContext } from "react";
import CreateComment from "../create-comment/CreateComment";
import classes from "./Post.module.css";
import { UserContext } from "../../../context/userContext";
import MessageIcon from "@material-ui/icons/Message";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";

const Post = () => {
  const [showCommentInput, setShowCommentInput] = useState(false);
  const [user] = useContext(UserContext).user;

  const onClickHandler = () => {
    setShowCommentInput((prevState) => !prevState);
  };

  return (
    <div className={classes.post_container}>
      <img src={user.photoURL} alt="posted_img" className={classes.post_img} />
      <div className={classes.post_comments}>
        <div className={classes.post_username}>
          {user.email.replace("@gmail.com", "")}
        </div>
        <div className={classes.post_comment}>I am getting strapped</div>
      </div>
      <div className={classes.msg_icon_container}>
        <MessageIcon className={classes.msg_icon} onClick={onClickHandler} />
        <FavoriteBorderIcon />
        {showCommentInput && <CreateComment />}
      </div>
    </div>
  );
};

export default Post;
