import React, { useState } from "react";
import CreateComment from "../create-comment/CreateComment";
import classes from "./Post.module.css";
import MessageIcon from "@material-ui/icons/Message";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import Comment from "../comment/Comment";

const Post = ({ key, id, caption, photoURL, username }) => {
  const [showCommentInput, setShowCommentInput] = useState(false);

  const onClickCommentInput = () => {
    setShowCommentInput((prevState) => !prevState);
  };

  return (
    <div className={classes.post_container}>
      <img src={photoURL} alt="posted_img" className={classes.post_img} />
      <div className={classes.post_comments}>
        <div className={classes.post_username}>{username}</div>
        <div className={classes.post_comment}>
          {comments ? (
            comments.map((comment) => (
              <Comment username={comment.username} comment={comment.comment} />
            ))
          ) : (
            <></>
          )}
        </div>
      </div>
      <div className={classes.msg_icon_container}>
        <MessageIcon
          className={classes.msg_icon}
          onClick={onClickCommentInput}
        />
        <FavoriteBorderIcon className={classes.heart_icon} />
      </div>
      {showCommentInput && <CreateComment />}
    </div>
  );
};

export default Post;
