import React, { useContext } from "react";
import classes from "./Button.module.css";
import { signInWithGoogle } from "../../services/auth";
import { UserContext } from "../../context/userContext";

export const SignInSignUpBtn = () => {
  const [, setUser] = useContext(UserContext).user;

  const buttonClickHandler = async () => {
    let signedInUser = await signInWithGoogle();
    if (signedInUser) {
      setUser(signedInUser);
    }
  };

  return (
    <button
      onClick={buttonClickHandler}
      id="signIn__signUp__btn"
      className={classes.signIn__signUp__btn}
    >
      sign in | sign up
    </button>
  );
};

export const CreatePostUploadBtn = ({ caption }) => {
  const onClickHandler = () => {};
  return (
    <button
      className={classes.createPost_upload_btn}
      onClick={onClickHandler}
      style={{
        color: caption ? "#000" : "lightgrey",
        border: "none",
        background: "transparent",
        cursor: "pointer",
      }}
    >
      Upload
    </button>
  );
};
